/// <reference types="react" />

import * as React from "react";

interface HTMLAttributesWeak extends React.HTMLAttributes<HTMLElement> {
  defaultValue?: any;
  onChange?: any;
}

export interface CascaderSelectProps extends HTMLAttributesWeak {
  /**
   * 样式类名的品牌前缀
   */
  prefix?: string;

  /**
   * 自定义类名
   */
  className?: string;

  /**
   * 自定义内联样式
   */
  style?: React.CSSProperties;

  /**
   * 选择框大小
   */
  size?: "small" | "medium" | "large";

  /**
   * 选择框形状
   */
  shape?: "normal" | "arrow-only";

  /**
   * 选择框占位符
   */
  placeholder?: string;

  /**
   * 是否禁用
   */
  disabled?: boolean;

  /**
   * 是否显示右侧的箭头
   */
  hasArrow?: boolean;

  /**
   * 是否显示清空按钮，该按钮可以清空当前选中的值，该属性仅在单选模式下有效
   */
  hasClear?: boolean;

  /**
   * 数据源，结构可参考下方说明
   */
  dataSource?: Array<any>;

  /**
   * （非受控）默认值
   */
  defaultValue?: string | Array<any>;

  /**
   * （受控）当前值
   */
  value?: string | Array<any>;

  /**
   * 选中值改变时触发的回调函数
   */
  onChange?: ((
    value: string | Array<any>,
    data: {} | Array<any>,
    extra: {}
  ) => void);

  /**
   * 初始下拉框是否显示
   */
  defaultVisible?: boolean;

  /**
   * 默认展开值，如果不设置，组件内部会根据 defaultValue/value 进行自动设置
   */
  defaultExpandedValue?: Array<any>;

  /**
   * 展开触发的方式
   */
  expandTrigger?: "click" | "hover";

  /**
   * 是否多选
   */
  multiple?: boolean;

  /**
   * 是否选中即发生改变, 该属性仅在单选模式下有效
   */
  changeOnSelect?: boolean;

  /**
   * 是否只能勾选叶子项的checkbox，该属性仅在多选模式下有效
   */
  canOnlyCheckLeaf?: boolean;

  /**
   * 父子节点是否选中不关联
   */
  checkStrictly?: boolean;

  /**
   * 文字区域宽度，当文字超过宽度时，结尾会以省略号显示，默认值为文字实际宽度
   */
  labelWidth?: string | number;

  /**
   * 每一列展示的个数
   */
  showItemCount?: string | number;

  /**
   * 选择框单选时展示结果的函数
   */
  displayRender?: ((label: Array<any>) => React.ReactNode);

  /**
   * 下拉框样式自定义类名
   */
  popupClassName?: string;

  /**
   * 下拉框挂载的容器节点
   */
  container?: string | (() => void);

  /**
   * 异步加载数据函数
   */
  loadData?: ((data: {}) => void);
}

export default class CascaderSelect extends React.Component<
  CascaderSelectProps,
  any
> {}
