/// <reference types="react" />

import * as React from "react";

export interface DropdownProps extends React.HTMLAttributes<HTMLElement> {
  /**
   * 样式类名的品牌前缀
   */
  prefix?: string;

  /**
   * 自定义类名
   */
  className?: string;

  /**
   * 自定义内联样式
   */
  style?: React.CSSProperties;

  /**
   * 弹层内容
   */
  children?: React.ReactNode;

  /**
   * 弹层当前是否可见
   */
  visible?: boolean;

  /**
   * 弹层默认是否可见
   */
  defaultVisible?: boolean;

  /**
   * 弹层在显示和隐藏时触发的回调函数
   */
  onVisibleChange?: ((visible: boolean, type: string, event: {}) => void);

  /**
   * 触发弹层显示或者隐藏的元素
   */
  trigger?: React.ReactNode;

  /**
   * 触发弹层显示或者隐藏的事件
   */
  triggerType?: "hover" | "click" | "focus";

  /**
   * 是否禁用，如果设置为true，那么trigger不能触发弹层的显示或隐藏
   */
  disabled?: boolean;

  /**
   * 弹层相对于trigger的定位, 详见[Overlay的定位部分](http://fusion-demo.alibaba-inc.com/components?type=next&themeId=next&name=overlay#demo-guide)
   */
  align?: string;

  /**
   * 弹层相对于trigger的定位的微调
   */
  offset?: Array<any>;

  /**
   * 悬浮状态下延时时间
   */
  delay?: number;

  /**
   * 弹层弹出后是否自动获取焦点
   */
  autoFocus?: boolean;

  /**
   * 是否带有遮罩
   */
  hasMask?: boolean;

  /**
   * 在弹层隐藏后是否保留当前的DOM
   */
  cache?: boolean;

  /**
   * 弹层显示前触发的回调函数
   */
  beforeOpen?: (() => void);

  /**
   * 弹层显示后触发的回调函数
   */
  afterOpen?: (() => void);

  /**
   * 弹层关闭前触发的回调函数
   */
  beforeClose?: (() => void);

  /**
   * 弹层关闭后触发的回调函数
   */
  afterClose?: (() => void);

  /**
   * 弹层定位完成后触发的回调函数
   */
  onPosition?: ((config: {}, node: {}) => void);

  /**
   * 动画的配置，如果设置为false，将关闭动画
   */
  animation?: boolean | {};
}

export default class Dropdown extends React.Component<DropdownProps, any> {}
