/// <reference types="react" />

import * as React from "react";

export interface SliderProps extends React.HTMLAttributes<HTMLElement> {
  /**
   * 样式前缀
   */
  prefix?: string;

  /**
   * 自定义传入的样式
   */
  className?: string;

  /**
   * 是否使用自适应高度
   */
  adaptiveHeight?: boolean;

  /**
   * 是否开启动效
   */
  animation?: boolean;

  /**
   * 是否显示箭头
   */
  arrows?: boolean;

  /**
   * 导航箭头大小
   */
  arrowSize?: "medium" | "large";

  /**
   * 导航箭头位置
   */
  arrowPos?: "inline" | "outer";

  /**
   * 导航箭头的方向
   */
  arrowDirection?: "horizontal" | "vertical";

  /**
   * 向后箭头
   */
  nextArrow?: React.ReactElement<any>;

  /**
   * 向后箭头
   */
  prevArrow?: React.ReactElement<any>;

  /**
   * 是否自动播放
   */
  autoplay?: boolean;

  /**
   * 自动播放的速度
   */
  autoplaySpeed?: number;

  /**
   * 是否启用居中模式
   */
  centerMode?: boolean;

  /**
   * 是否显示导航锚点
   */
  dots?: boolean;

  /**
   * 导航锚点到位置
   */
  dotsDirection?: "horizontal" | "vertical";

  /**
   * 是否可拖拽
   */
  draggable?: boolean;

  /**
   * 是否使用淡入淡出效果
   */
  fade?: boolean;

  /**
   * 是否使用无穷循环模式
   */
  infinite?: boolean;

  /**
   * 初始被激活的轮播图
   */
  initialSlide?: number;

  /**
   * 是否启用懒加载
   */
  lazyLoad?: boolean;

  /**
   * 轮播方向
   */
  slideDirection?: "horizontal" | "vertical";

  /**
   * 同时展示的图片数量
   */
  slidesToShow?: number;

  /**
   * 同时滑动到图片数量
   */
  slidesToScroll?: number;

  /**
   * 轮播速度
   */
  speed?: number;

  /**
   * 跳转到指定的轮播图（受控）
   */
  slickGoTo?: number;

  /**
   * 轮播切换后的回调函数
   */
  afterChange?: ((index: number) => void);

  /**
   * 轮播切换前的回调函数
   */
  beforeChange?: ((currentIndex: number, nextIndex: number) => void);
}

export default class Slider extends React.Component<SliderProps, any> {}
