/// <reference types="react" />

import * as React from "react";

interface HTMLAttributesWeak extends React.HTMLAttributes<HTMLElement> {
  onChange?: any;
  onSelect?: any;
}

export interface TagProps extends HTMLAttributesWeak {
  /**
   * 样式前缀
   */
  prefix?: string;

  /**
   * 标签的形态
   */
  shape?: "selectable" | "deletable" | "interactive" | "link" | "readonly";

  /**
   * 标签的类型
   */
  type?: "normal" | "primary" | "secondary";

  /**
   * 标签的尺寸（large 尺寸为兼容表单场景 large = medium）
   */
  size?: "medium" | "small" | "large";

  /**
   * 标签是否禁用
   */
  disabled?: boolean;

  /**
   * 标签附加显示的重复次数
   */
  count?: number;

  /**
   * 选择型标签是否被选中（受控）
   */
  selected?: boolean;

  /**
   * 选型型标签默认是否被选中（非受控）
   */
  defaultSelected?: boolean;

  /**
   * 可删除标签是否被关闭（受控）
   */
  closed?: boolean;

  /**
   * 可删除标签默认是否被关闭（非受控）
   */
  defaultClosed?: boolean;

  /**
   * 互动型标签是否被标记过（受控）
   */
  marked?: boolean;

  /**
   * 标签暂存的值
   */
  value?: string | Array<any> | {} | boolean;

  /**
   * 是否开启动效
   */
  animation?: boolean;

  /**
   * 互动型标签被 mark 的数量变化时的回调
   */
  onChange?: ((count: number) => void);

  /**
   * 可删除标签被关闭时的回调
   */
  onClose?: ((value: string, closed: boolean) => void);

  /**
   * 可选择标签被选中时的回调
   */
  onSelect?: ((selected: boolean) => void);

  /**
   * 标签出现后执行的回调
   * Function() => void
   */
  afterAppear?: (() => void);

  /**
   * 标签关闭后执行的回调
   * Function() => void
   */
  afterClose?: (() => void);
}

export default class Tag extends React.Component<TagProps, any> {}
