
/// <reference types="react" />

import React from 'react';

export interface CardProps {

    /**
     * 样式的品牌前缀
     */
    prefix?: string;

    /**
     * 卡片的标题
     */
    title?: any;

    /**
     * 卡片的副标题
     */
    subTitle?: any;

    /**
     * 是否显示标题的前缀线
     */
    titlePrefixLine?: any;

    /**
     * 是否显示标题栏的下划线
     */
    titleBottomLine?: any;

    /**
     * 内容区域的固定高度
     */
    bodyHeight?: string | number;

    /**
     * 标题区域的用户自定义内容
     */
    extra?: any;

    /**
     * 自定义样式
     */
    className?: string;

}

export default class Card extends React.Component<CardProps, any> {

}
