
/// <reference types="react" />

import React from 'react';

export interface FeedbackProps {

    /**
     * 样式类名的品牌前缀
     */
    prefix?: string;

    /**
     * 自定义类名
     */
    className?: string;

    /**
     * 自定义内联样式
     */
    style?: React.CSSProperties;

    /**
     * 反馈类型
     */
    type?: 'success' | 'error' | 'prompt' | 'help' | 'loading';

    /**
     * 反馈外观
     */
    shape?: 'inline' | 'addon' | 'toast';

    /**
     * 反馈大小
     */
    size?: 'medium' | 'large';

    /**
     * 标题
     */
    title?: any;

    /**
     * 内容
     */
    children?: any;

    /**
     * 是否显示
     */
    visible?: any;

}

export default class Feedback extends React.Component<FeedbackProps, any> {

}
